const Mousetrap = require("./libs/mousetrap/mousetrap");

module.exports = class BetterRyverKeybinds {
    constructor(window) {
        this.window = window;

        this.registerKeybinds(window.betterryver.internalConfig.loader.debug);
    }

    registerKeybinds(debug=false) {
        let gCO = this.window.betterryver.getCurrentOrganisation;
        if (debug) {
            Mousetrap.bind("alt+r", () => {
                gCO().reload();
            });

            Mousetrap.bind("alt+i", () => {
                gCO().openDevTools();
            });
        }
    }
}
